INSERT INTO book VALUES ('9786589711377', '1984', 'George Orwell', 'Secker & Warburg', '1949-6-8', 'Science Fiction', '1499', '10', 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/c3/1984first.jpg/440px-1984first.jpg', '1984 is a dystopian novella by George Orwell published in 1949, which follows the life of Winston Smith, a low ranking member of the Party, who is frustrated by the omnipresent eyes of the party, and its ominous ruler Big Brother. Big Brother controls every aspect of people''s lives.');
